package java_example;

public class Brajesh_add_three_decimal_numbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double x;// create a bucket x which can only store decimal value
		 double y;// create a bucket y which can only store decimal vale
		 double z;// Create a bucket z which can only store decimal value
		 x=100.50; // fill 100.50 in bucket x
		 y=200.50; // fill 200.50 in bucket y
		 z=300.50; // fill 300.50 in bucket z
		 double sum; // create a bucket sum which can store decimal value
		 sum = x +y+z; // Going to the memory and getting the value from the bucket x,y, and z.and put in the right hand side and ADD.Put the result in the bucket sum.
		 System.out.println(sum);//print whatever is in bucket sum
		 //System.out.println(x);// print whatever is in bucket x
	}

}
