package java_example;

public class Brajesh_add_two_decimal_numbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		double x;// create a bucket x which can only store decimal value
		 double y;// create a bucket y which can only store decimal vale
		 x=100.50; // fill 100 in bucket x
		 y=200.50; // fill 200 in bucket y
		 
		 double sum; // create a bucket sum which can store integer value
		 sum = x +y; // 
		 System.out.println(sum);//print whatever is in bucket sum
		 System.out.println(x);// print whatever is in bucket x
		
	}

}
