
public class mul_two_numbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// TODO Auto-generated method stub
		//create the buckets
				int x; //create a bucket x
				int y; //create a bucket y
				int z; //create a bucket z

				int mul; //create a bucket mul

			//fill the buckets
				x=50;//fill the bucket x with 50
				y=4;//fill the bucket y with 4
				z=5;// fiil the bucket zwith 5
				
				mul=x*y*z; //it will substitute x, y and z from buckets and muliply and fill the bucket mul
				

				//print the bucket
				System.out.println(mul);
	}

}
